import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar-user',
  templateUrl: './navbar-user.component.html',
  styleUrls: ['./navbar-user.component.css']
})
export class NavbarUserComponent {
  toggleSearchBar() {
    let searchBar = document.getElementById("searchBar");
    // @ts-ignore
    searchBar.style.display = searchBar.style.display !== "none" ? "none" : "flex";
  }

  showFilters = false;

  toggleFiltersWindow() {
    let filtersWindow = document.getElementById("filters");
    // @ts-ignore
    filtersWindow.style.display = filtersWindow.style.display !== "none" ? "none" : "flex";
  }

  // Method to handle selected filters
  handleFilters(selectedFilters: any) {
    // Handle selected filters here
  }
}
<div class="user">

  <div class="option">
    <img ngSrc='../../assets/map.png' alt="Carte" height="200" width="200">
    <span>Carte</span>
  </div>

  <div class="clickableOption" (click)="toggleSearchBar()">
    <img ngSrc='../../assets/search.png' alt="Rechercher" height="200" width="200">
    <span>Rechercher</span>
  </div>

  <div class="option">
    <img ngSrc='../../assets/compte.png' alt="Mon compte" height="200" width="200">
    <span>Compte</span>
  </div>

</div>

<!-- Search bar initially hidden -->
<div class="search-bar" id="searchBar">
  <input type="text" placeholder="Rechercher...">
  <a routerLink="../home-user">
    <img ngSrc='../../assets/searchbar.png' alt="Rechercher" height="5" width="5">
  </a>

  <div class="clickableOption" id="filters" (click)="toggleFiltersWindow()">
    <img ngSrc='../../assets/filter.png' alt="Filtres" height="5" width="5">
    <span>Filtres</span>
  </div>
</div>

