import {Component, EventEmitter, Output, OnInit } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';
import { FilterService } from './filters-service.model';
import { BatimentService } from '../carte/batiment.service';

@Component({
  selector: 'app-filters',
  templateUrl: './filters.component.html',
  styleUrls: ['./filters.component.css']
})

export class FiltersComponent implements OnInit{
  hideFilters() {
    const filtersWindow = document.getElementById("filters-window");
    // @ts-ignore
    filtersWindow.style.display = "none";
  }
  clearSelection(): void {
    this.selectedType = ''; // Réinitialisez la valeur de la sélection à une chaîne vide
    this.selectedDepartement = '';
    this.selectedRegion = '';
  }



  regions: string[] = [];
  departements: string[] = []; // Initialisation de la propriété departements
  communes: string[] = [];
  types: string[] = [];
  @Output() typeSelected: EventEmitter<string> = new EventEmitter<string>();

  selectedRegion: string = '';
  selectedDepartement: string = '';
  selectedCommune: string = '';
  selectedType: string = '';

  constructor(private filterService: FilterService, private batimentService: BatimentService) { }

  ngOnInit(): void {
    this.filterService.getAllTypes().subscribe(types => this.types = types);
    this.filterService.getAllRegions().subscribe(regions => this.regions = regions);
    this.filterService.getAllDepartements().subscribe(departements => this.departements = departements);
    this.filterService.getAllCommunes().subscribe(communes => this.communes = communes);
  }



  onTypeSelected(): void {
    if (this.selectedType !== null) { // Vérifiez si la valeur est différente de null
      this.batimentService.setSelectedType(this.selectedType);
    }
    if (this.selectedDepartement !== null) {
      this.batimentService.setSelectedType(this.selectedDepartement);
    }
    /*
    if (this.selectedRegion !== null) {
      this.batimentService.setSelectedType(this.selectedRegions);
    }*/
  }



}

<div class="popup" id="filters-window">

  <h1>Filtres</h1>

  <div class="form-group">
    <label for="buildingType">Rechercher par type</label>
    <div class="dropdown">
      <select id="buildingType" class="form-control" [(ngModel)]="selectedType" (change)="onTypeSelected()">
        <option *ngFor="let type of types" [value]="type">{{ type }}</option>
      </select>
      <img ngSrc='../../assets/arrow.png' alt="Fleche" height="24" width="24">
    </div>
  </div>

  <div class="form-group">
    <label for="region">Rechercher par région</label>
    <div class="dropdown">
      <select id="region" class="form-control" [(ngModel)]="selectedRegion">
        <option *ngFor="let region of regions" [value]="region">{{ region }}</option>
      </select>
      <img ngSrc='../../assets/arrow.png' alt="Fleche" height="24" width="24">
    </div>
  </div>

  <div class="form-group">
    <label for="departement">Rechercher par département</label>
    <div class="dropdown">
        <select id="departement" class="form-control" [(ngModel)]="selectedDepartement" (change)="onTypeSelected()">
          <option *ngFor="let departement of departements" [value]="departement">{{ departement }}</option>
        </select>
      <img ngSrc='../../assets/arrow.png' alt="Fleche" height="24" width="24">
    </div>
  </div>


  <div class="button-container">
    <app-button text="Annuler" [buttonClass]="'btn-light'" (click)="clearSelection()"></app-button>
    <app-button text="Valider" [buttonClass]="'btn-blue'" (click)="hideFilters()"></app-button>
  </div>

</div>
