import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  toggleStatus(status: number) {
    if (status === 0) {  // visitor
    }

    else if (status === 1) {  // user
      const searchBar = document.getElementById("searchBar");
      // @ts-ignore
      searchBar.style.display = "flex";

      const optAccount = document.getElementById("opt-account");
      // @ts-ignore
      optAccount.style.display = "flex";

      const btnConnect = document.getElementById("btn-connect");
      // @ts-ignore
      btnConnect.style.display = "none";

      const btnBat = document.getElementById("btn-add-bat");
      // @ts-ignore
      btnBat.style.display = "block";
    }

    else if (status === 2) {  // admin
      const optSuggest = document.getElementById("opt-suggest");
      // @ts-ignore
      optSuggest.style.display = "flex";

      const optAccount = document.getElementById("opt-account");
      // @ts-ignore
      optAccount.style.display = "flex";

      const btnConnect = document.getElementById("btn-connect");
      // @ts-ignore
      btnConnect.style.display = "none";
    }
  }

  toggleFilters() {
    const filtersWindow = document.getElementById("filters-window");
    // @ts-ignore
    if (filtersWindow.style.display === "none") {
      // @ts-ignore
      filtersWindow.style.display = "block";
    } else {
      // @ts-ignore
      filtersWindow.style.display = "none";
    }
  }

  toggleMenu() {
    const menu = document.getElementById("menu");
    // @ts-ignore
    if (menu.style.display === "none") {
      // @ts-ignore
      menu.style.display = "flex";
    } else {
      // @ts-ignore
      menu.style.display = "none";
    }
  }
}
<div class="navbar">

  <!-- Option "Carte" -->
  <div class="option">
    <img ngSrc='../../assets/home.png' alt="Accueil" routerLink="../home" height="40" width="40">
  </div>


  <!-- Option "Suggestion", initialement cachée -->
  <div class="option" id="opt-suggest">
    <img ngSrc='../../assets/home.png' alt="Suggestion" height="24" width="24">
    <span>Suggestions</span>
  </div>


  <!-- Bar de recherche, initialement cachée -->
  <div id="searchBar">
    <input type="text" placeholder="Rechercher...">
    <img id="clickSearch" ngSrc='../../assets/searchbar.png' alt="Rechercher" routerLink="../home" height="24" width="24">

    <!-- Option "Filtres", initialement cachée -->
    <a class="option" id="filters" (click)="toggleFilters()">
      <img ngSrc='../../assets/filter.png' alt="Filtres" height="24" width="24">
      <span>Filtres</span>
    </a>
  </div>


  <!-- Option "Menu", initialement cachée -->
  <a class="option" id="opt-account" (click)="toggleMenu()">
    <img ngSrc='../../assets/menu.png' alt="Menu" height="40" width="40">
  </a>


  <!-- Bouton "Se connecter" -->
  <!-- <a routerLink="../home-user" id="btn-connect"> -->
    <app-button id="btn-connect" text="Se connecter" [buttonClass]="'btn-light'" (click)="toggleStatus(1)"></app-button>
</div>

<div class="menu" id="menu">
  <div class="menu-option">
    <img ngSrc='../../assets/compte.png' alt="Mon compte" height="24" width="24">
    <a routerLink="../account">Mon compte</a>
  </div>

  <div class="menu-option">
    <img ngSrc='../../assets/parametres.png' alt="Parametres" height="24" width="24">
    <a routerLink="../settings">Paramètres</a>
  </div>
</div>

<app-filters></app-filters>
<a routerLink="../add-bat">
  <app-button id="btn-add-bat" text="Ajouter un bâtiment" [buttonClass]="'btn-blue'"></app-button>
</a>
